mapper.update:
	UPDATE TBL_DEPARTMENT
   SET USE_YN = 'N'
   
   	
       
           WHERE DEPT_CODE IN
           
               #{item}
           
           OR DEPT_CODE = #{deptCode}
       
       
       
           WHERE DEPT_CODE = #{deptCode}

	UPDATE TBL_EMPLOYEE 
	SET DEPT_CODE=#{deptCode} 
	WHERE EMP_ID IN
	
        #{item}

UPDATE TBL_EMPLOYEE
SET DEPT_LEADER = 'N'
WHERE EMP_ID = #{id}

